import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { DOTS, usePagination } from '../hooks/usePagination';

const useStyles = makeStyles((theme) => ({
    root: {
        '& > * + *': {
            marginTop: theme.spacing(2),
        },
    },
}))

const Paginations = ({ onPageChange, totalCount, siblingCount = 1, currentPage, pageSize, onPrevious, onNext }) => {
    const classes = useStyles();

    const paginationRange = usePagination({ currentPage, totalCount, siblingCount, pageSize });


    return (
        <div>
            {currentPage === 1 ? null : (
                <Button onClick={onPrevious}>flecha</Button>
            )}
            {paginationRange.map(pageNumber => {
                if(pageNumber === DOTS){
                    return <div>&#8230;</div>
                }

                return(
                    <Button onClick={() => onPageChange(pageNumber)}>{pageNumber}</Button>
                );
            })};
            {currentPage === 45 ? null : (
                <Button onClick={onNext}>flecha</Button>
            )}
        </div>
    );
};

export default Paginations;
