import React, { useState, useEffect } from 'react';
import axios from 'axios';
import getPokemons from '../services/pokemons';
import Paginations from './Paginations';
import { Paper, Grid, makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.secondary,
    },
}));

const PokemonsDisplay = () => {
    const classes = useStyles();
    const [pokemons, setPokemons] = useState([]);
    const [offset, setOffset] = useState(0);
    const [limit, setLimit] = useState(20);

    useEffect(() => {
        const fetchPokemons = async () => {
            //FETCHES 20 OBJECTS THAT CONTAIN AN URL TO AN INDIVIDUAL POKEMON
            const results = await getPokemons.getPokemons(limit, offset);
            let urlArray = [];
            results.results.forEach(async (r) => {
                //FETCHES EACH POKEMON URL AND STORES ITS DATA ON pokemons STATE
                const pokemon = await axios.get(r.url);
                urlArray.push(pokemon.data);
                if(urlArray.length > 0){
                    setPokemons([...urlArray]);
                }
            });
        }
        fetchPokemons();
    }, [offset, limit]);

    console.log(pokemons);

    return (
        <div className={classes.root}>
            <Grid container spacing={3}>
                {pokemons && pokemons.map(p => (
                    <Grid item xs={3} key={p.name}>
                        <Paper className={classes.paper}>{p.id}</Paper>
                        <Paper className={classes.paper}>{p.name}</Paper>
                    </Grid>
                ))}
            </Grid>
            <Paginations setOffset={setOffset} setLimit={setLimit}/>
        </div>
    );
};

export default PokemonsDisplay;
