import React, { useEffect, useState } from 'react';
import axios from 'axios';
import getPokemons from '../services/pokemons';
import { Grid, makeStyles, Paper } from '@material-ui/core';
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
      image: {
          width: 100,
          height: 'auto',
          alignSelf: 'center',
    },
}));

const Types = ({ selectedType }) => {
    const classes = useStyles();
    
    const [pokemonsType, setPokemonsType] = useState([]);

    useEffect(() => {
        const fetchPokemons = async () => {
            const result = await getPokemons.getIndividualType(selectedType);
            let urlArray = [];
            console.log(result);
            result.pokemon.forEach(async (r) => {
                try {
                    let poke = await axios.get(r.pokemon.url);
                    urlArray.push(poke.data);
                    setPokemonsType([...urlArray]);
                } catch (error) {
                    console.log(error);
                }
            });
        }
        fetchPokemons();
    }, [selectedType]);
    

    return (
        <div className={classes.root}>
            <Grid container spacing={3}>
                {pokemonsType && pokemonsType.map(p => (
                    <Grid item xs={3} key={p.name} >
                        <Link to={`/pokemons/${p.id}`}>
                            <Paper className={classes.paper}>
                                #{p.id}<br></br>
                                {p.name}
                                <img className={classes.image} alt={`${p.name}'s sprite`} src={p.sprites.other["dream_world"]["front_default"]}/>
                            </Paper>
                        </Link>
                    </Grid>
                ))}
            </Grid>
        </div>
    );
};

export default Types;
