import React from 'react';
import { Button, makeStyles, AppBar, Toolbar, Typography, Box, InputBase } from '@material-ui/core';
import { Link } from 'react-router-dom';
import Filter from './Filter';

const useStyles = makeStyles(() => ({
  root: {
    display: 'flex',
    flexGrow: 1
  },
  filter: {
    alignItems: 'flex-end'
  }
}))

const AppBarPokemon = ({ setPokemon, pokemon }) => {
  const classes = useStyles();
  return (
    <Box className={classes.root}>
      <AppBar position="static" color="secondary" className={classes.appBar}>
        <Toolbar variant="dense">
          <Typography variant="h6" color="inherit">
              <Button color="inherit" component={Link} to="/" data-cy='home-button'>
                Home
              </Button>
              <Button color="inherit" component={Link} to="/pokemons" data-cy='pokedex-button'>
                Pokedex
              </Button>
              <InputBase >
                <Filter setPokemon={setPokemon} pokemon={pokemon}/>
              </InputBase>
          </Typography>
        </Toolbar>
      </AppBar>
    </Box>
  );
}

export default AppBarPokemon;
