import axios from 'axios';

const baseUrl = 'https://pokeapi.co/api/v2/';

const getPokemons = async (limit, offset) => {
    const response = await axios.get(`${baseUrl}pokemon?limit=${limit}&offset=${offset}`);
    return response.data;
};

const getTypes = async () => {
    const response = await axios.get(`${baseUrl}type`);
    return response.data;
}

const getIndividualType = async (type) => {
    const response = await axios.get(`${baseUrl}/type/${type}`);
    return response.data;
}

// eslint-disable-next-line import/no-anonymous-default-export
export default { getPokemons, getTypes, getIndividualType };
