import React from 'react';
import Paginations from './Paginations';
import { Grid, makeStyles, Paper } from '@material-ui/core';
import { Link } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
      image: {
          width: 100,
          height: 'auto',
          alignSelf: 'center',
    },
}));

const PokemonsDisplay = ({ pokemon, setPokemon, pokemons, setOffset, setLimit }) => {
    const classes = useStyles();

    const handleClick = (poke) => {
        console.log(poke);
        let array = [];
        array.push(poke);
        console.log(array)
        setPokemon([...array]);
        console.log(pokemon);
    };

    return (
        <div className={classes.root}>
            <Grid container spacing={3}>
                {pokemons && pokemons.map(p => (
                    <Grid item xs={3} key={p.name} component={Link} to={`/pokemons/${p.id}`} onClick={() => handleClick(p)}>
                        <Paper className={classes.paper}>
                            #{p.id}<br></br>
                            {p.name}
                            <img className={classes.image} alt={`${p.name}'s sprite`} src={p.sprites.other["dream_world"]["front_default"]}/>
                        </Paper>
                    </Grid>
                ))}
            </Grid>
            <Paginations setOffset={setOffset} setLimit={setLimit}/>
        </div>
    );
};

export default PokemonsDisplay;
