import React from 'react';
import { Link } from 'react-router-dom';
import { Button, makeStyles } from '@material-ui/core';
import pokedex from '../img/Pokédex_logo.png';
import pikachu from '../img/pikachu(1).png';
import Filter from './Filter';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        height: 600,
        flexDirection: 'column',
        flexWrap: 'wrap',
        flexGrow: 1,
        justifyContent: 'center'
    },
    logo: {
        width: 500,
        height: 'auto',
        alignSelf: 'center'
    },
    pokedex: {
        backgroundColor: '#E5709B',
        height: 150,
        alignSelf: 'center',
        width: width,
        borderRadius: width/2 
    },
    pikachu: {
        height: 130,
        width: 'auto'
    }
}));

const width = 250

const Home = ({ setPokemons, pokemons, CapsFirstLetter }) => {
    const classes = useStyles();
    return (
        <div className={classes.root}>
            <img className={classes.logo} src={pokedex} alt='pokedex logo'/>
            <Filter setPokemons={setPokemons} pokemons={pokemons} CapsFirstLetter={CapsFirstLetter}/>
            <Button data-cy="pokedex-pikachu" className={classes.pokedex} component={Link} to="/pokemons">
                <img  className={classes.pikachu} src={pikachu} alt='imagen pikachu'/>
            </Button>
        </div>
    );
};

export default Home;
