import React from 'react';
import signUp from '../services/signUp';
import { Field, Formik, Form, ErrorMessage } from 'formik';
import { Button } from '@material-ui/core';
import * as yup from 'yup';
import { useHistory } from 'react-router';
import TextField from './TextField';
import { useDispatch } from 'react-redux';
import { setUser } from '../reducers/userReducer';
import { notificationError } from '../reducers/notificationReducer';

const validationSchema = yup.object().shape({
    username: yup
        .string()
        .required()
        .min(5, 'Username must contain at least 5 characters')
        .max(20, 'Username must not contain more than 20 characters'),
    password: yup
        .string()
        .required()
        .min(5, 'Username must contain at least 5 characters')
        .max(20, 'Username must not contain more than 20 characters'),
    name: yup
        .string()
        .required()
})

const SignUp = () => {
    const dispatch = useDispatch();
    const history = useHistory();

    const onSubmit = async (event) => {
        event.preventDefault();
        
        const credentials = {
            username: event.username,
            password: event.password,
            name: event.name
        };

        try {
            const user = await signUp.signUp(credentials);
            if(user){
                window.localStorage.setItem('loggedUser', JSON.stringify(user))
                dispatch(setUser(user));
                history.push('/');
            };
        } catch (error) {
            console.log(error);
            dispatch(notificationError('Your account was not created, try again!'))
            return null;
        };
    };

    return (
        <Formik
            initialValues={{
                username:'',
                password:'',
                name:''
            }}
            onSubmit={onSubmit}
            validationSchema={validationSchema}
        >
            <Form className="form ui">
                <label>"Username"</label><br></br>
                <Field
                    placeholder="Username"
                    name="username-signUp"
                    component={TextField}
                />
                <label>"Password"</label><br></br>
                <Field
                    placeholder="Password"
                    name="password-signUp"
                    component={TextField}
                />
                <label>"Name"</label><br></br>
                <Field
                    placeholder="Name"
                    name="name-signUp"
                    component={TextField}
                />
                <Button
                    type="submit"
                    name="submit-button-signUp"
                >
                    Sign Up
                </Button>
            </Form>
        </Formik>
    );
};

export default SignUp;
