import React from 'react';
import { Paper, makeStyles } from '@material-ui/core';
import { useParams } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    paper: {
      padding: theme.spacing(2),
      textAlign: 'center',
      color: theme.palette.text.secondary,
      backgroundColor: '#E5709B'
    },
    image: {
        width: 100,
        height: 'auto',
        alignSelf: 'center',
    },
    p: {
        textAlign: 'center',
    }
}));

const IndividualPokemon = ({ pokemons }) => {
    const id = useParams().id;
    const pokemon = pokemons.find(p => p.id === Number(id));
    console.log(pokemon);
    const classes = useStyles();
    return (
                <Paper className={classes.paper}>
                    <p className={classes.p}>#{pokemon.id}</p><br></br>
                    <p className={classes.p}>{pokemon.name}</p>
                    <img className={classes.image} alt={`${pokemon.name}'s sprite`} src={pokemon.sprites.other["dream_world"]["front_default"]}/>
                </Paper>
    );
};

export default IndividualPokemon;
