import React, { useState, useEffect } from 'react';
import { useDebounce } from 'use-debounce';
import getPokemons from '../services/pokemons';
import { Switch, Route, Redirect } from 'react-router-dom';
import IndividualPokemon from './IndividualPokemon';

const Filter = ({ setPokemons, pokemons, CapsFirstLetter }) => {
    const [filter, setFilter] = useState('');
    const [debounced] = useDebounce(filter, 1600);

    const handleChange = (event) => {
        const pokemon = event.target.value;
        const lowercasePokemon = pokemon.toLowerCase();
        setFilter(lowercasePokemon);
    };

    useEffect(() => {
        const fetchPokemon = async () => {
            try {
                const searchedPokemon = await getPokemons.getPokemonByName(debounced);
                let array = [];
                array.push(searchedPokemon);
                setPokemons([...array]);
                if(pokemons.length === 1){
                    console.log(pokemons[0].name);
                    return (
                        <Switch>
                            <Redirect to={`/pokemons/${pokemons[0].name}`} />
                            <Route to='/pokemons/:name'>
                                <IndividualPokemon pokemons={pokemons} CapsFirstLetter={CapsFirstLetter}/>
                            </Route>
                        </Switch>
                    )
                }
            } catch (error) {
                console.log(error);
                return null;
            }
            console.log(pokemons);
        };
        fetchPokemon();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[debounced])

    return (
        <div>
            <input placeholder='Search...' value={filter} onChange={handleChange}/>
        </div>
    );
};

export default Filter;
