import React, { useState } from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { Link } from 'react-router-dom';
import { Button, MenuItem, Select, InputLabel, FormControl, makeStyles } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  button: {
    display: 'block',
    marginTop: theme.spacing(2),
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
}));

const AppBarPokemon = ({ types, setSelectedType }) => {
  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const handleClose = () => {
    setOpen(false);
  };

  const handleOpen = () => {
    setOpen(true);
  };

  const handleChange = (event) => {
    setSelectedType(event.target.value);
  };

  return (
    <div>
      <AppBar position="static">
        <Toolbar variant="dense">
          <Typography variant="h6" color="inherit">
              <Button component={Link} color="inherit" to="/">
                    Home
              </Button>
              <Button component={Link} color="inherit" to="/pokemons">
                    Pokedex
              </Button>
              <FormControl className={classes.formControl} color='inherit'>
                <InputLabel>Types</InputLabel>
                <Select
                  color='inherit'
                  open={open}
                  onClose={handleClose}
                  onOpen={handleOpen}
                  value=''
                  placeholder='Types'
                  onChange={handleChange}
                >
                  {types.map(t => <MenuItem value={t} key={t}>{t}</MenuItem>)}
                </Select>
              </FormControl>
          </Typography>
        </Toolbar>
      </AppBar>
    </div>
  );
}

export default AppBarPokemon;
