import React from 'react';
import { Paper, makeStyles } from '@material-ui/core';
import { useParams } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        backgroundColor: '#E5709B'
    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: theme.palette.text.secondary,
      backgroundColor: '#E5709B'
    },
    image: {
        width: 450,
        height: 'auto',
        alignSelf: 'flex-start',
    },
    p: {
        textAlign: 'center',
    }
}));

const IndividualPokemon = ({ pokemons }) => {
    const id = useParams().id;
    const pokemon = pokemons.find(p => p.id === Number(id));
    console.log(pokemon);
    const classes = useStyles();
    return (
        <div className={classes.root}>
                <Paper className={classes.paper}>
                    <img className={classes.image} alt={`${pokemon.name}'s sprite`} src={pokemon.sprites.other["dream_world"]["front_default"]}/>
                    <p className={classes.p}>#{pokemon.id}</p>
                    <p className={classes.p}>{pokemon.name}</p>
                </Paper>
        </div>
    );
};

export default IndividualPokemon;
