import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router';
import getPokemons from '../services/pokemons';
import { Button } from '@material-ui/core';

const Filter = ({ setPokemons, pokemons, CapsFirstLetter }) => {
    const [filter, setFilter] = useState('');
    const history = useHistory();

    const handleChange = (event) => {
        event.preventDefault();
        console.log(event)
        const pokemon = event.target.filterInput.value;
        const lowercasePokemon = pokemon.toLowerCase();
        setFilter(lowercasePokemon);
    };

    useEffect(() => {
        const fetchPokemon = async () => {
            try {
                const searchedPokemon = await getPokemons.getPokemonByName(filter);
                let array = [];
                array.push(searchedPokemon);
                setPokemons([...array]);
                if(pokemons.length === 1){
                    history.push(`/pokemons/${pokemons[0].name}`)
                }
            } catch (error) {
                console.log(error);
                return null;
            }
            console.log(pokemons);
        };
        fetchPokemon();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[filter])

    return (
        <div>
            <form onSubmit={handleChange}>
                <input placeholder='Search...' name="filterInput"/>
                <Button>Search</Button>
            </form>
        </div>
    );
};

export default Filter;
