import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import { Button, makeStyles } from '@material-ui/core';
import { Link } from 'react-router-dom';
import Filter from './Filter';

const useStyles = makeStyles(() => ({
  root: {
    display: 'flex',
    flexDirection: 'row',
    flexWrap: 'nowrap',
    justifyContent: 'center'
  },
  filter: {
    alignSelf: 'flex-end'
  }
}))

const AppBarPokemon = ({ setPokemons }) => {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <AppBar position="static" color="secondary">
        <Toolbar variant="dense">
          <Typography variant="h6" color="inherit">
              <Button color="inherit" component={Link} to="/" data-cy='home-button'>
                Home
              </Button>
              <Button color="inherit" component={Link} to="/pokemons" data-cy='pokedex-button'>
                Pokedex
              </Button>
              <Filter className={classes.filter} setPokemons={setPokemons} />
          </Typography>
        </Toolbar>
      </AppBar>
    </div>
  );
}

export default AppBarPokemon;
