import React, { useState, useEffect } from 'react';
import './App.css';
import PokemonsDisplay from './components/PokemonsDisplay';
import { Switch, Route } from 'react-router-dom';
import Home from './components/Home';
import AppBarPokemon from './components/AppBar';
import getPokemons from './services/pokemons';
import Types from './components/Types';
import IndividualPokemon from './components/IndividualPokemon';
import axios from 'axios';

const App = () => {
  const [types, setTypes] = useState([]);
  const [selectedType, setSelectedType] = useState('');
  const [pokemons, setPokemons] = useState([]);
  const [offset, setOffset] = useState(0);
  const [limit, setLimit] = useState(20);
  const [pokemonsType, setPokemonsType] = useState([]);

  useEffect(() => {
      const fetchPokemons = async () => {
          //FETCHES 20 OBJECTS THAT CONTAIN AN URL TO AN INDIVIDUAL POKEMON
          const results = await getPokemons.getPokemons(limit, offset);
          let urlArray = [];
          results.results.forEach(async (r) => {
              //FETCHES EACH POKEMON URL AND STORES ITS DATA ON pokemons STATE
              const pokemonNow = await axios.get(r.url);
              urlArray.push(pokemonNow.data);
              if(urlArray.length > 0){
                  setPokemons([...urlArray]);
              };
          });
      }
      fetchPokemons();
  }, [offset, limit]);

  useEffect(() => {
    const fetchTypes = async () => {
        const result = await getPokemons.getTypes();
        let typesArray = [];
        result.results.forEach(r => {
            typesArray.push(r.name);
            setTypes([...typesArray]);
        });
    };
    fetchTypes();
  }, []);

  console.log(types);

  return (
    <div>
      <AppBarPokemon types={types} setSelectedType={setSelectedType}/>
      <Switch>
        <Route path="/pokemons/:id">
          <IndividualPokemon pokemons={pokemons} />
        </Route>
        <Route path="/types/:id">
          <IndividualPokemon pokemons={pokemons} />
        </Route>
        <Route path="/pokemons">
          <PokemonsDisplay pokemons={pokemons} setOffset={setOffset} setLimit={setLimit}/>
        </Route>
        <Route path="/types">
          <Types selectedType={selectedType} pokemonsType={pokemonsType} setPokemonsType={setPokemonsType}/>
        </Route>
        <Route path="/" component={Home}/>
      </Switch>
    </div>
  )
}

export default App;
