import React from 'react';
import { Paper, makeStyles } from '@material-ui/core';
import { useParams } from 'react-router-dom';
import pokedex from '../img/pokedex1.png';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        backgroundColor: '#222222',
        justifyContent: 'center',
        padding: 10
    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: theme.palette.text.secondary,
      flexDirection: 'row',
      flexWrap: 'nowrap',
      position: 'absolute',
      marginTop: 37
    },
    paper2: {
        padding: theme.spacing(1),
        textAlign: 'center',
        color: theme.palette.text.secondary,
        flexDirection: 'row',
        flexWrap: 'nowrap',
        position: 'absolute',
        marginTop: 360
      },
    image: {
        height: 200,
        width: 'auto',
        alignSelf: 'flex-start',
    },
    text: {
        textAlign: 'center',
        fontSize: 13,
        fontFamily: 'Roboto, monospace',
        fontWeight: 'bold',
    },
    name: {
        textAlign: 'flexStart',
        marginRight: 240,
        fontSize: 14,
        fontFamily: 'Roboto, monospace',
        fontWeight: 'bold',
    }
}));

const IndividualPokemon = ({ pokemons, CapsFirstLetter }) => {
    const name = useParams().name;
    const pokemon = pokemons.find(p => p.name === name.toString());
    console.log(pokemon);
    const classes = useStyles();
    return (
        <div className={classes.root}>
                <img src={pokedex} alt='pokedex'/>
                <div className={classes.paper}>
                    {pokemon.sprites.other["dream_world"]["front_default"] !== null ? 
                    <img className={classes.image} alt={`${pokemon.name}'s sprite`} src={pokemon.sprites.other["dream_world"]["front_default"]}/> :
                    <img className={classes.image} alt={`${pokemon.name}'s sprite`} src={pokemon.sprites.other["official-artwork"]["front_default"]}/>}
                </div>
                <div className={classes.paper2}>
                    <p className={classes.name}>#{pokemon.id}</p>
                    <p className={classes.name}>{CapsFirstLetter(pokemon.name)}</p>
                    {pokemon.stats.map(s => (
                        <p className={classes.text} key={s.stat.name}>{CapsFirstLetter(s.stat.name)}: {s.base_stat}</p>
                    ))}
                </div>
        </div>
    );
};

export default IndividualPokemon;
