import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import { DOTS, usePagination } from '../hooks/usePagination';

const width = 10

const useStyles = makeStyles((theme) => ({
    root: {
        '& > * + *': {
            marginTop: theme.spacing(2),
        },
        display: 'flex',
        flexDirection: 'row',
        flexWrap: 'nowrap',
        justifyContent: 'center'
    },
    button: {
        width: width,
        borderRadius: width/2,
        backgroundColor: '#E5709B',
    },
    active: {
        width: width,
        borderRadius: width/2,
        backgroundColor: 'white',
        color: 'black'
    }
}));

const Paginations = ({ onPageChange, totalCount, siblingCount = 1, currentPage, pageSize, onPrevious, onNext }) => {
    const classes = useStyles();

    const paginationRange = usePagination({ currentPage, totalCount, siblingCount, pageSize });


    return (
        <div className={classes.root}>
            {currentPage === 1 ? null : (
                <Button className={classes.button} onClick={onPrevious}>flecha</Button>
            )}
            {paginationRange.map(pageNumber => {
                if(pageNumber === DOTS){
                    return <div>&#8230;</div>
                }

                return(
                    <div>
                        {pageNumber === currentPage ? (
                            <Button className={classes.active} onClick={() => onPageChange(pageNumber)} >{pageNumber}</Button>) : (
                            <Button className={classes.button} onClick={() => onPageChange(pageNumber)} >{pageNumber}</Button>
                        )}
                    </div>
                );
            })};
            {currentPage === 45 ? null : (
                <Button className={classes.button} onClick={onNext}>flecha</Button>
            )}
        </div>
    );
};

export default Paginations;
