import React, { useState } from 'react';
import { useHistory } from 'react-router';
import getPokemons from '../services/pokemons';
import { Button } from '@material-ui/core';

const Filter = ({ setPokemons, pokemons, CapsFirstLetter }) => {
    const [filter, setFilter] = useState('');
    const history = useHistory();

    const updateFilter = async (value) => {
        await setFilter(() => ({
            filter: value
        }))
    }

    const handleChange = async (event) => {
        event.preventDefault();
        console.log(event.target.filterInput.value)
        const pokemon = event.target.filterInput.value;
        const lowercasePokemon = pokemon.toLowerCase();
        updateFilter(lowercasePokemon);
        console.log(filter);
        try {
            const searchedPokemon = await getPokemons.getPokemonByName(filter);
            console.log(searchedPokemon);
            let array = [];
            array.push(searchedPokemon);
            console.log(array);
            setPokemons([...array]);
            console.log(pokemons);
            if(pokemons.length === 1){
                history.push(`/pokemons/${pokemons[0].name}`)
            }
        } catch (error) {
            console.log(error);
            return null;
        }
        console.log(pokemons);
    };
    //
    return (
        <div>
            <form onSubmit={handleChange}>
                <input placeholder='Search...' name="filterInput"/>
                <Button type="submit" >Search</Button>
            </form>
        </div>
    );
};

export default Filter;
