import React from 'react';
import { Paper, makeStyles } from '@material-ui/core';
import { useParams } from 'react-router-dom';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        backgroundColor: '#222222',
        justifyContent: 'center',
        padding: 10
    },
    paper: {
      padding: theme.spacing(1),
      textAlign: 'center',
      color: theme.palette.text.secondary,
      backgroundColor: '#E5709B',
      flexDirection: 'row',
      flexWrap: 'nowrap',
    },
    image: {
        width: 280,
        height: 'auto',
        alignSelf: 'flex-start',
    },
    text: {
        textAlign: 'center',
        fontSize: 20,
        fontFamily: 'Roboto, monospace',
        fontWeight: 'bold',
    },
    type: {
        height: 25,
        weight: 33,
        fontSize: 12,
        fontFamily: 'Roboto, monospace',
        textAlign: 'center',
        fontWeight: 'bold',
        color: 'white',
        backgroundColor: '#222222',
        position: 'abosolute',
        top: 50,
        right: 25
    }
}));

const IndividualPokemon = ({ pokemons, CapsFirstLetter }) => {
    const name = useParams().name;
    const pokemon = pokemons.find(p => p.name === name.toString());
    console.log(pokemon);
    const classes = useStyles();
    return (
        <div className={classes.root}>
                <Paper className={classes.paper} elevation={8}>
                    {pokemon.sprites.other["dream_world"]["front_default"] !== null ? 
                    <img className={classes.image} alt={`${pokemon.name}'s sprite`} src={pokemon.sprites.other["dream_world"]["front_default"]}/> :
                    <img className={classes.image} alt={`${pokemon.name}'s sprite`} src={pokemon.sprites.other["official-artwork"]["front_default"]}/>}
                    <p className={classes.text}>#{pokemon.id}</p>
                    <p className={classes.text}>{CapsFirstLetter(pokemon.name)}</p>
                    {pokemon.stats.map(s => (
                        <p className={classes.text} key={s.stat.name}>{CapsFirstLetter(s.stat.name)}: {s.base_stat}</p>
                    ))}
                </Paper>
                {pokemon.types.map(t => <div className={classes.type}>{t.type.name}</div>)}
        </div>
    );
};

export default IndividualPokemon;
