import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core';
import Filter from './Filter';

const useStyles = makeStyles(() => ({
    type: {
        height: 25,
        fontSize: 18,
        fontFamily: 'Roboto, monospace',
        justifyContent: 'center',
        textAlign: 'center',
        fontWeight: 'bold',
        color: 'white',
        backgroundColor: '#c2185b',
        borderRadius: 60,
        marginBottom: 9
    }
}))

const Favorite = ({ CapsFirstLetter, setError }) => {
    const classes = useStyles();
    const [pokemon, setPokemon] = useState({});

    console.log(pokemon);

    if(!pokemon){
        return (
            <div>
                <p>You don't have a favorite pokémon right now! Please pick One!</p>
                <Filter setPokemon={setPokemon} setError={setError}/>
            </div>

        )
    };

    return (
        <div>
            <p>Your Favorite Pokémon Is:</p>
            <img src={pokemon.sprites["front_default"]} alt={`${pokemon.name}'s sprite`}></img>
            <div>
                {pokemon.types.map(t => <div className={classes.type}>{t.type.name.toUpperCase()}</div>)}
                <p>Abilities: {pokemon.abilities.map(a => <p>{CapsFirstLetter(a.ability.name)}</p>)}</p>
                <p>Height: {pokemon.height}</p>
                <p>Weight: {pokemon.weight}</p>
                <p>If you want to pick a new favorite Pokémon, use this search bar!</p>
                <Filter setPokemon={setPokemon} setError={setError} />
            </div>
        </div>
    );
};

export default Favorite;
