import React, { useState, useEffect } from 'react';
import { useDebounce } from 'use-debounce';
import getPokemons from '../services/pokemons';

const Filter = ({ setPokemons }) => {
    const [filter, setFilter] = useState('');
    const [debounced] = useDebounce(filter, 1100);

    const handleChange = (event) => {
        const pokemon = event.target.value;
        const lowercasePokemon = pokemon.toLowerCase();
        setFilter(lowercasePokemon);
    };

    useEffect(() => {
        const fetchPokemon = async () => {
            const searchedPokemon = await getPokemons.getPokemonByName(debounced);
            setPokemons(searchedPokemon);
        };
        fetchPokemon();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    },[debounced])

    return (
        <input placeholder='Search...' value={filter} onChange={handleChange}/>
    );
};

export default Filter;
