import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router';
import getPokemons from '../services/pokemons';
import { Button } from '@material-ui/core';
import IndividualPokemon from './IndividualPokemon';

const Filter = () => {
    const [filter, setFilter] = useState('');
    const history = useHistory();

    const CapsFirstLetter = (str) => {
        let toCapitalize = str.split(/-/);
    
        for (let i = 0; i < toCapitalize.length; i++) {
          toCapitalize[i] = toCapitalize[i][0].toUpperCase() + toCapitalize[i].substr(1);
        };
    
        const joined = toCapitalize.join(' ');
    
        return joined;
    };

    useEffect(() => {
        const fetch = async () => {
            try {
                const searchedPokemon = await getPokemons.getPokemonByName(filter);
                console.log(searchedPokemon);
                console.log(filter);
                if(searchedPokemon.name === filter){
                    return (
                        <div>
                            <IndividualPokemon pokemon={searchedPokemon} CapsFirstLetter={CapsFirstLetter}/>
                            {history.push(`/pokemons/${searchedPokemon.name}`)}
                        </div>
                    )
                }
            } catch (error) {
                console.log(error);
                return null;
            }
        } 
        fetch();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [filter])

    const handleChange = async (event) => {
        event.preventDefault();
        console.log(event.target.filterInput.value)
        const pokemon = event.target.filterInput.value;
        const lowercasePokemon = pokemon.toLowerCase();
        setFilter(lowercasePokemon);
    };//

    return (
        <div>
            <form onSubmit={handleChange}>
                <input placeholder='Search...' name="filterInput"/>
                <Button type="submit" >Search</Button>
            </form>
        </div>
    );
};

export default Filter;
