import React, { useState, useEffect } from 'react';
import { makeStyles, Button } from '@material-ui/core';
import Filter from './Filter';
import { useHistory } from 'react-router';
import getPokemons from '../services/pokemons';

const useStyles = makeStyles(() => ({
    type: {
        height: 25,
        fontSize: 18,
        fontFamily: 'Roboto, monospace',
        justifyContent: 'center',
        textAlign: 'center',
        fontWeight: 'bold',
        color: 'white',
        backgroundColor: '#c2185b',
        borderRadius: 60,
        marginBottom: 9
    }
}))

const Favorite = ({ CapsFirstLetter, setError }) => {
    const classes = useStyles();
    const [pokemon, setPokemon] = useState({});
    const [filter, setFilter] = useState('');
    const history = useHistory();

    useEffect(() => {
        const fetch = async () => {
            try {
                if(filter){
                    const searchedPokemon = await getPokemons.getPokemonByName(filter);
                    console.log(searchedPokemon);
                    setPokemon(searchedPokemon);
                    if(searchedPokemon.name === filter){
                        history.push(`/search/${searchedPokemon.name}`);
                    };
                }
            } catch (error) {
                console.log(error);
                setError('That pokémon doesnt exist! Try Again!');
                setTimeout(() => {
                    setError(null);
                }, 3500)
                return null;
            }
        } 
        fetch();
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [filter])

    const handleChange = async (event) => {
        event.preventDefault();
        console.log(event.target.filterInput.value)
        const pokemon = event.target.filterInput.value;
        const lowercasePokemon = pokemon.toLowerCase();
        setFilter(lowercasePokemon);
    };//

    console.log(pokemon);

    const isEmpty = (obj) => {
        return Object.keys(obj).length === 0;
    }

    if(isEmpty(pokemon)){
        return (
            <div>
                <p>You don't have a favorite pokémon right now! Please pick One!</p>
                <div>
                    <form onSubmit={handleChange}>
                        <input placeholder='Search...' name="filterInput"/>
                        <Button type="submit" >Search</Button>
                    </form>
                </div>
            </div>

        )
    };

    return (
        <div>
            {!isEmpty(pokemon) && 
                <div>
                    <p>Your Favorite Pokémon Is:</p>
                    <img src={pokemon.sprites["front_default"]} alt={`${pokemon.name}'s sprite`}></img>
                    <div>
                        {pokemon.types.map(t => <div className={classes.type} key={t.type.name}>{t.type.name.toUpperCase()}</div>)}
                        <div>Abilities: {pokemon.abilities.map(a => <p key={a.ability.name}>{CapsFirstLetter(a.ability.name)}</p>)}</div>
                        <p>Height: {pokemon.height}</p>
                        <p>Weight: {pokemon.weight}</p>
                        <p>If you want to pick a new favorite Pokémon, use this search bar!</p>
                        <div>
                            <form onSubmit={handleChange}>
                                <input placeholder='Search...' name="filterInput"/>
                                <Button type="submit" >Search</Button>
                            </form>
                        </div>
                    </div>
                </div>
            }
        </div>
    );
};

export default Favorite;
