import getPokemons from '../services/pokemons';
import axios from 'axios';
import { loadPokemonsFromLS, savePokemonsList } from '../utils/localStoragePokemons';
import produce from 'immer';

const pokemonsReducer = produce((draft, action) => {
    switch(action.type){
        case 'INIT_POKEMONS':
            draft.pokemons = action.pokemons
            return draft
        default:
            return draft;
    };
})

export const thunkYourMom = (limit, offset) => {
    return async dispatch => {
        try {
            const pokemons = loadPokemonsFromLS(limit, offset);
            dispatch(recieveAllPokemons(pokemons));
        } catch (error) {
            const pokemons = await getPokemons.getPokemons(limit, offset);
            let pokemonsArray = [];
            let pokemonsObject = {};
            pokemons.results.forEach(async (r, i) => {
                //FETCHES EACH POKEMON URL AND STORES ITS DATA ON pokemons STATE
                const pokemonNow = await axios.get(r.url);
                pokemonsArray.push(pokemonNow.data);
                pokemonsObject[i] = pokemonNow.data
            });
            savePokemonsList(limit, offset, pokemonsObject);
            dispatch(recieveAllPokemons(pokemonsArray));
        };
    };    
};

export const fetchEverything = (limit, offset) => {
    return thunkYourMom(limit, offset);
};

const recieveAllPokemons = (pokemons) => {
    return {
        type: 'INIT_POKEMONS',
        pokemons,
    };
};
